package com.chua.evergrocery.database.dao.impl;import org.hibernate.criterion.Junction;import org.hibernate.criterion.Restrictions;import org.springframework.stereotype.Repository;import com.chua.evergrocery.database.dao.SystemVariableDAO;import com.chua.evergrocery.database.entity.SystemVariable;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   24 June 2018 */@Repositorypublic class SystemVariableDAOImpl		extends AbstractDAO<SystemVariable, Long> 		implements SystemVariableDAO {	@Override	public SystemVariable findByTag(String tag) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));		conjunction.add(Restrictions.eq("tag", tag));				return findUniqueResult(null, null, null, conjunction);	}}
