package com.chua.evergrocery.database.dao.impl;import org.hibernate.criterion.Junction;import org.hibernate.criterion.Order;import org.hibernate.criterion.Restrictions;import org.springframework.stereotype.Repository;import com.chua.evergrocery.database.dao.XReadingDAO;import com.chua.evergrocery.database.entity.XReading;import com.chua.evergrocery.objects.ObjectList;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   23 March 2019 */@Repositorypublic class XReadingDAOImpl		extends AbstractDAO<XReading, Long> 		implements XReadingDAO {		@Override	public ObjectList<XReading> findAllWithPagingAndOrder(int pageNumber, int resultsPerPage,			Order[] orders) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));				return findAllByCriterion(pageNumber, resultsPerPage, null, null, null, orders, conjunction);	}}
