package com.chua.evergrocery.database.dao.impl;import java.util.Date;import java.util.List;import org.hibernate.criterion.Junction;import org.hibernate.criterion.Order;import org.hibernate.criterion.Restrictions;import org.springframework.stereotype.Repository;import com.chua.evergrocery.database.dao.ZReadingDAO;import com.chua.evergrocery.database.entity.ZReading;import com.chua.evergrocery.objects.ObjectList;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   22 March 2019 */@Repositorypublic class ZReadingDAOImpl		extends AbstractDAO<ZReading, Long> 		implements ZReadingDAO {	@Override	public ObjectList<ZReading> findAllWithPagingAndOrder(int pageNumber, int resultsPerPage,			Order[] orders) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));				return findAllByCriterion(pageNumber, resultsPerPage, null, null, null, orders, conjunction);	}	@Override	public ZReading findByReadingDate(Date readingDate) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));		conjunction.add(Restrictions.eq("readingDate", readingDate));				return findUniqueResult(null, null, null, conjunction);	}	@Override	public List<ZReading> findAllByReadingDateRangeWithOrder(Date dateStart, Date dateEnd,			Order[] orders) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));		conjunction.add(Restrictions.ge("readingDate", dateStart));		conjunction.add(Restrictions.le("readingDate", dateEnd));				return findAllByCriterionList(null, null, null, orders, conjunction);	}}
