package com.chua.evergrocery.database.entity;import javax.persistence.Basic;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Table;import com.chua.evergrocery.database.entity.base.BaseObject;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   24 June 2018 */@Entity(name = "SystemVariable")@Table(name = SystemVariable.TABLE_NAME)public class SystemVariable extends BaseObject {	private static final long serialVersionUID = -7993288409469979799L;		public static final String TABLE_NAME = "system_variable";		private String tag;		private String content;	@Basic	@Column(name = "tag")	public String getTag() {		return tag;	}	public void setTag(String tag) {		this.tag = tag;	}	@Basic	@Column(name = "content")	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}}
