package com.chua.evergrocery.database.service.impl;import java.util.Date;import java.util.List;import org.hibernate.criterion.Order;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.chua.evergrocery.database.dao.CashTransferDAO;import com.chua.evergrocery.database.entity.CashTransfer;import com.chua.evergrocery.database.service.CashTransferService;import com.chua.evergrocery.enums.Status;import com.chua.evergrocery.objects.ObjectList;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   30 June 2018 */@Servicepublic class CashTransferServiceImpl		extends AbstractService<CashTransfer, Long, CashTransferDAO> 		implements CashTransferService {	@Autowired	protected CashTransferServiceImpl(CashTransferDAO dao) {		super(dao);	}	@Override	public ObjectList<CashTransfer> findRecentOrRequestingByRelatedUserWithPaging(int pageNumber, int resultsPerPage,			Long userId) {		return dao.findByRelatedUserAndDaysAgoOrStatusWithPagingAndOrder(pageNumber, resultsPerPage, userId, 3, new Status[] { Status.REQUESTING }, null);	}	@Override	public ObjectList<CashTransfer> findAllByRelatedUserAndStatusWithPagingOrderByLatest(int pageNumber, int resultsPerPage,			Long userId, Status[] status) {		return dao.findByRelatedUserAndDaysAgoOrStatusWithPagingAndOrder(pageNumber, resultsPerPage, userId, null, status, new Order[] { Order.desc("updatedOn") });	}	@Override	public List<CashTransfer> findAllTransferredByUserAndDate(Long userId, Date dateFrom) {		return dao.findAllByUserStatusAndDateTransferred(userId, new Status[] { Status.TRANSFERRED }, dateFrom);	}}
