package com.chua.evergrocery.database.service.impl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.chua.evergrocery.database.dao.SystemVariableDAO;import com.chua.evergrocery.database.entity.SystemVariable;import com.chua.evergrocery.database.service.SystemVariableService;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   24 June 2018 */@Servicepublic class SystemVariableServiceImpl		extends AbstractService<SystemVariable, Long, SystemVariableDAO> 		implements SystemVariableService {	@Autowired	protected SystemVariableServiceImpl(SystemVariableDAO dao) {		super(dao);	}	@Override	public String findByTag(String tag) {		return dao.findByTag(tag).getContent();	}	@Override	public Boolean updateByTag(String tag, String content) {		final SystemVariable systemVariable = dao.findByTag(tag);		systemVariable.setContent(content);		return update(systemVariable);	}}
