package com.chua.evergrocery.database.service.impl;import org.hibernate.criterion.Order;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.chua.evergrocery.database.dao.XReadingDAO;import com.chua.evergrocery.database.entity.XReading;import com.chua.evergrocery.database.service.XReadingService;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   23 March 2019 */@Servicepublic class XReadingServiceImpl		extends AbstractService<XReading, Long, XReadingDAO> 		implements XReadingService {	@Autowired	protected XReadingServiceImpl(XReadingDAO dao) {		super(dao);	}		@Override	public XReading getLatestXReading() {		return dao.findAllWithPagingAndOrder(0, 1, new Order[] { Order.desc("readingDate") }).getList().get(0);	}}
