package com.chua.evergrocery.database.service.impl;import java.util.Date;import java.util.List;import org.hibernate.criterion.Order;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.chua.evergrocery.database.dao.ZReadingDAO;import com.chua.evergrocery.database.entity.ZReading;import com.chua.evergrocery.database.service.ZReadingService;import com.chua.evergrocery.utility.DateUtil;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   22 March 2019 */@Servicepublic class ZReadingServiceImpl		extends AbstractService<ZReading, Long, ZReadingDAO> 		implements ZReadingService {	@Autowired	protected ZReadingServiceImpl(ZReadingDAO dao) {		super(dao);	}	@Override	public ZReading getLatestZReading() {		return dao.findAllWithPagingAndOrder(0, 1, new Order[] { Order.desc("readingDate") }).getList().get(0);	}	@Override	public ZReading findByReadingDate(Date readingDate) {		return dao.findByReadingDate(DateUtil.floorDay(readingDate));	}	@Override	public List<ZReading> findAllZReadingByDateRangeOrderByReadingDate(Date dateStart, Date dateEnd) {		return dao.findAllByReadingDateRangeWithOrder(dateStart, dateEnd, new Order[] { Order.asc("readingDate") });	}}
